;&nbsp;&nbsp;<\\/code><code class=\\\"cpp plain\\\">}<\\/code><\\/div>\\r\\n<div class=\\\"line number15 index14 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number16 index15 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ Find the end of the interval<\\/code><\\/div>\\r\\n<div class=\\\"line number17 index16 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number18 index17 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">right = findLastElementSmallerThan(x+1, array, 0, len-1);<\\/code><\\/div>\\r\\n<div class=\\\"line number19 index18 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number20 index19 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp keyword bold\\\">if<\\/code> <code class=\\\"cpp plain\\\">(array[right] != x) {<\\/code><\\/div>\\r\\n<div class=\\\"line number21 index20 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ Element does not exist, since the end of the interval isn\'t X<\\/code><\\/div>\\r\\n<div class=\\\"line number22 index21 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number23 index22 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp keyword bold\\\">return<\\/code> <code class=\\\"cpp plain\\\">0;<\\/code><\\/div>\\r\\n<div class=\\\"line number24 index23 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp plain\\\">}<\\/code><\\/div>\\r\\n<div class=\\\"line number25 index24 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number26 index25 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ No need to go over the entire array, just left of the end of the interval<\\/code><\\/div>\\r\\n<div class=\\\"line number27 index26 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ Also, if the array starts with X, the return index is -1<\\/code><\\/div>\\r\\n<div class=\\\"line number28 index27 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ We need to pass the appropriate parameters, i.e. \\\"-1\\\" and \\\"right\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number29 index28 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number30 index29 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">left = findLastElementSmallerThan(x, array, -1, right) + 1;<\\/code><\\/div>\\r\\n<div class=\\\"line number31 index30 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number32 index31 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp comments\\\">\\/\\/ Result<\\/code><\\/div>\\r\\n<div class=\\\"line number33 index32 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number34 index33 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp keyword bold\\\">return<\\/code> <code class=\\\"cpp plain\\\">right - left + 1;<\\/code><\\/div>\\r\\n<div class=\\\"line number35 index34 alt2\\\"><code class=\\\"cpp plain\\\">}<\\/code><\\/div>\\r\\n<div class=\\\"line number36 index35 alt1\\\">&nbsp;<\\/div>\\r\\n<div class=\\\"line number37 index36 alt2\\\"><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">findLastElementSmallerThan(<\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">x, <\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">*array, <\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">left, <\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">right) {<\\/code><\\/div>\\r\\n<div class=\\\"line number38 index37 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp keyword bold\\\">while<\\/code> <code class=\\\"cpp plain\\\">(left &lt; right) {<\\/code><\\/div>\\r\\n<div class=\\\"line number39 index38 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp color1 bold\\\">int<\\/code> <code class=\\\"cpp plain\\\">mid = (right == 0) ? 0 : (left + right) \\/ 2 + 1;<\\/code><\\/div>\\r\\n<div class=\\\"line number40 index39 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code>&nbsp;<\\/div>\\r\\n<div class=\\\"line number41 index40 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp keyword bold\\\">if<\\/code> <code class=\\\"cpp plain\\\">(array[mid] &gt;= x) {<\\/code><\\/div>\\r\\n<div class=\\\"line number42 index41 alt1\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp plain\\\">right = mid - 1;<\\/code><\\/div>\\r\\n<div class=\\\"line number43 index42 alt2\\\"><code class=\\\"cpp spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"cpp plain\\\">} <\\/code><code class=\\\"cpp keyword bold